;(function($){$.rotateList=function(selector,settings){var lista=$(selector),started=0,counter=0,timerOn=0,rotationNumber=0,startedFunction=false;var config={minLines:5,speed:2000,colorSelectedRow:'#F1F1F1',colorBackgroundRow:'#FFFFFF',colorText:'#444444',colorSelectedText:'#000000',externalFunction:null,stopOnComplete:false};if(settings){$.extend(config,settings)}function listSize(){return lista.size()}function setListColor(){lista.parent().css({'background-color':config.colorBackgroundRow,'color':config.colorText})}function startStop(){if(started===0){startRolagem();started=1}else if(started===1){stopRolagem();started=0}}function startRolagem(){if(timerOn==1){return}else{if(listSize()>=config.minLines){counter=setInterval(rolaLista,config.speed);timerOn=1}else{startExternalFunction()}}}function stopRolagem(){if(timerOn==1){clearInterval(counter);timerOn=0}}function rolaLista(){var itens=$(selector),prev=$(itens).first();updateColorLine(prev);$.unique(prev).each(function(i){$(this).delay(i*600).slideUp(function(){$(this).appendTo(this.parentNode).slideDown()})});rotationNumber++;if(listSize()==rotationNumber){startExternalFunction();rotationNumber=0}verifyStopOnComplete()}function updateColorLine(linha){linha.css({'background-color':config.colorBackgroundRow,'color':config.colorText});linha.next().css({'background-color':config.colorSelectedRow,'color':config.colorSelectedText})}function verifyStopOnComplete(){if(listSize()==rotationNumber&&config.stopOnComplete){startStop()}}function startExternalFunction(){if(startedFunction==false){if(config.externalFunction)config.externalFunction();startedFunction=true}}lista.mouseenter(function(){startStop();$(this).css('cursor','wait')}).mouseleave(function(){startStop();$(this).css('cursor','default')});setListColor();startStop();return this}})(jQuery);